import './App.css';
import { useState , useEffect} from "react"; 
import axios from 'axios';

function App() {

  const [jugador, setJugador] = useState('')
  const [dorsal, setDorsal] = useState('')
  const [posicion, setPosicion] = useState('')
  const [listaJugadores, setListaJugadores] = useState([])

  useEffect(() => {
    axios.get("http://localhost:3001/api/get").then((response) => {
      setListaJugadores(response.data)
    })
  })

  const submitData = () => {
    console.log(jugador)
    axios.post("http://localhost:3001/api/insert", {
      jugador: jugador, 
      dorsal: dorsal, 
      posicion: posicion
    }).then(() => {
      alert('Successfull insert')
    })
  }

  return (
    <div className="App">
      <h1>Batidora FC</h1>
      <div className='form'>
        <label>Jugador: </label>
        <input type='text' name='jugador' onChange={(e) => (
          setJugador(e.target.value)
        )}/>
        <label>Dorsal: </label>
        <input type='text' name='dorsal' onChange={(e) => (
          setDorsal(e.target.value)
        )}/>
        <label>Posicion: </label>
        <input type='text' name='posicion' onChange={(e) => (
          setPosicion(e.target.value)
        )}/>
        <button onClick={submitData}>Submit</button>

        {listaJugadores.map((value) => {
            return <div>
                <div class="card-container">
	<span class="pro">PRO</span>
	<img class="round" src="https://randomuser.me/api/portraits/women/79.jpg" alt="user" />
	<h3>Ricky Park</h3>
	<h6>New York</h6>
	<p>User interface designer and <br/> front-end developer</p>
	<div class="buttons">
		<button class="primary">
			Message
		</button>
		<button class="primary ghost">
			Following
		</button>
	</div>
	<div class="skills">
		<h6>Skills</h6>
		<ul>
			<li>UI / UX</li>
			<li>Front End Development</li>
			<li>HTML</li>
			<li>CSS</li>
			<li>JavaScript</li>
			<li>React</li>
			<li>Node</li>
		</ul>
	</div>
</div>

<footer>
	<p>
		Created with <i class="fa fa-heart"></i> by
		<a target="_blank" href="https://florin-pop.com">Florin Pop</a>
		- Read how I created this
		<a target="_blank" href="https://florin-pop.com/blog/2019/04/profile-card-design">here</a>
		- Design made by
		<a target="_blank" href="https://dribbble.com/shots/6276930-Profile-Card-UI-Design">Ildiesign</a>
	</p>
</footer>
              </div>
        })}
      </div>
    </div>
  );
}

export default App;
