import './App.css';
import { useState , useEffect} from "react"; 
import axios from 'axios';

function App() {

  const [jugador, setJugador] = useState('')
  const [dorsal, setDorsal] = useState('')
  const [posicion, setPosicion] = useState('')
  const [listaJugadores, setListaJugadores] = useState([])

  useEffect(() => {
    axios.get("http://localhost:3001/api/get").then((response) => {
      setListaJugadores(response.data)
    })
  })

  const submitData = () => {
    console.log(jugador)
    axios.post("http://localhost:3001/api/insert", {
      jugador: jugador, 
      dorsal: dorsal, 
      posicion: posicion
    }).then(() => {
      alert('Successfull insert')
    })
  }

  return (
    <div className="App">
      <h1>CRUD App</h1>
      <div className='form'>
        <label>Jugador: </label>
        <input type='text' name='jugador' onChange={(e) => (
          setJugador(e.target.value)
        )}/>
        <label>Dorsal: </label>
        <input type='text' name='dorsal' onChange={(e) => (
          setDorsal(e.target.value)
        )}/>
        <label>Posicion: </label>
        <input type='text' name='posicion' onChange={(e) => (
          setPosicion(e.target.value)
        )}/>
        <button onClick={submitData}>Submit</button>

        {listaJugadores.map((value) => {
            return <div class="card">
            <div class="img-avatar">

            </div>
            <div class="card-text">
              <div class="portada">

              </div>
              <div class="title-total">   
                <div class="title">----------</div>
                <h2>{value.nombre_apellido}</h2>

            <div class="desc">{value.posicion} | {value.dorsal}</div>
            <div class="actions">
              <button><i class="far fa-heart"></i></button>
              <button><i class="far fa-envelope"></i></button>
              <button><i class="fas fa-user-friends"></i></button>
            </div></div>

            </div>
          </div>
        })}
      </div>
    </div>
  );
}

export default App;
