import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";
import imagen from './assets/logo3.jpg'


function App() {
  const [jugador, setJugador] = useState("");
  const [dorsal, setDorsal] = useState("");
  const [posicion, setPosicion] = useState("");
  const [listaJugadores, setListaJugadores] = useState([]);


  useEffect(() => {
    axios.get("http://localhost:3001/api/get").then((response) => {
      setListaJugadores(response.data);
    });
  }, []);


  //Multer
  const send = event => {
    console.log('hey')
  }

  const submitData = () => {
    console.log(jugador);
    axios
      .post("http://localhost:3001/api/insert", {
        jugador: jugador,
        dorsal: dorsal,
        posicion: posicion,
      })
      .then(() => {
        alert("Successfull insert");
      });
  };


  return (
    <div className="App">
      <h1 className='titulo'>Batidora FC</h1>

      <div className="form">
        <label className='input-title'>Jugador: </label>
        <input
          type="text"
          name="jugador"
          onChange={(e) => setJugador(e.target.value)}
        />
        <label className='input-title'>Dorsal: </label>
        <input
          type="text"
          name="dorsal"
          onChange={(e) => setDorsal(e.target.value)}
        />
        <label className='input-title'>Posicion: </label>
        <input
          type="text"
          name="posicion"
          onChange={(e) => setPosicion(e.target.value)}
        />
        <button className='submit-button' onClick={submitData}>Agregar jugador</button>

        <form action="#">
          <div className="flex">
            <label htmlFor="name">Name</label>
            <input type="text" id="name"></input>
          </div>
          <div className="flex">
            <label htmlFor="file">File</label>
            <input type="file" id="file" accept=".jpg"></input>
          </div>
        </form>
        <button onClick={send}>Send it!</button>

      </div>
        {listaJugadores.map((value) => {
          return (
            <div>
              <div className="card-container">
                <span className="pro">{value.dorsal}</span>
                <img className="round" src={imagen} alt="jugador" />
                <h3>{value.nombre_apellido}</h3>
                <h6>{value.posicion}</h6>
                <p>
                  Informacion extra <br /> Estadisticas
                </p>
                <div className="buttons">
                  <button className="primary">Estadisticas</button>
                  <button className="primary ghost">+1</button>
                </div>
                <div className="skills"></div>
              </div>
            </div>
          );
        })}
    </div>
  );
}

export default App;
