import React from 'react';
import '../App.css';
import { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import imagen from '../assets/logo3.jpg';
import axios from "axios";
import '../App.css';

function Jugador() {

    const [perfil, setPerfil] = useState('');

    useEffect( (id) => {
        axios.get(`http://localhost:3001/jugadores/:${id}`)
        .then((response) => {
            setPerfil(response)
        });
    })

    return (
        <div className="jugadores-title">
        <div className="jugadores-container">
          {setPerfil.map((value, key) => {
            return (
              <div>
                <div className="card-container">
                  <span className="pro">{value.dorsal}</span>
                  <img className="round" src={imagen} alt="jugador" />
                  <h3>{value.nombre_apellido}</h3>
                  <h6>{value.posicion}</h6>
                  <p>
                    Informacion extra <br /> Estadisticas
                  </p>
                  <div className="buttons">
                    <button className="primary" onClick={redirect}>
                      Estadisticas
                    </button>
                    <button className="primary ghost">+1</button>
                  </div>
                  <div className="footer-card"></div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    )
}

export default Jugador;
