import React from "react";
import "../App.css";
import { useState, useEffect } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import { Link } from "react-router-dom";
import imagen from "../assets/logo3.jpg";
import axios from "axios";
import "../App.css";
import { useHistory } from "react-router-dom";

function Jugadores() {
  let history = useHistory();

  const [listaJugadores, setListaJugadores] = useState([]);
  const [searchValue, setSearchValue] = useState("");

  useEffect(() => {
    axios.get("http://localhost:3001/jugadores").then((response) => {
      setListaJugadores(response.data);
    });
  }, []);

  const plantillaStyle = {
    fontSize: "35px",
  };

  const handleInputChange = (e) => {
    setSearchValue(e.target.value);
  };

  const filteredList = listaJugadores.filter(
    (player) => searchValue === "" || player.nombre_apellido == searchValue.includes(searchValue)
  );

  return (
    <div className="jugadores-title">
      <div className="form-1">
        <input
          type="text"
          name="name"
          id="searchBar"
          autocomplete="off"
          value={searchValue}
          onChange={handleInputChange}
          required
        />
        <label for="name" className="label-name">
          <span className="content-name">Name</span>
        </label>
      </div>
      <h4 style={plantillaStyle}>Plantilla</h4>
      <div className="jugadores-container">
        {filteredList.map((value, key) => {
          return (
            <div>
              <div className="card-container">
                <span className="pro">{value.dorsal}</span>
                <img className="round" src={imagen} alt="jugador" />
                <h3>{value.nombre_apellido}</h3>
                <h6>{value.posicion}</h6>
                <p>
                  Informacion extra <br /> Estadisticas
                </p>
                <div className="buttons">
                  <Link to={`/jugadores/${value.id}`}>
                    <button type="submit" className="primary">
                      Estadisticas
                    </button>
                  </Link>

                  <button className="primary ghost">+1</button>
                </div>
                <div className="footer-card"></div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Jugadores;
