import React from 'react';
import '../App.css';
import { useState, useEffect } from "react";
import imagen from '../assets/logo3.jpg';
import axios from "axios";


function CrearJugador() {

    const [jugador, setJugador] = useState("");
    const [dorsal, setDorsal] = useState("");
    const [posicion, setPosicion] = useState("");
    const [file, setFile] = useState("");
    
    const [newDorsal, setNewDorsal] = useState(0);
    const [newPosicion, setNewPosicion] = useState('');

  
    const [listaJugadores, setListaJugadores] = useState([]);
  
  
    const mostrarJugadores = () => {
      axios.get("http://localhost:3001/jugadores").then((response) => {
        setListaJugadores(response.data)
      })
    }
  
    const agregarJugador = () => {
      axios.post("http://localhost:3001/create", {
          jugador: jugador,
          dorsal: dorsal,
          posicion: posicion,
          file: file,
        }).then(() => {
          setListaJugadores([
            ...listaJugadores,
            {
              jugador: jugador,
              dorsal: dorsal,
              posicion: posicion,
          }])
      });
    };
  
    const actualizarDorsal = (id) => {
      axios.put("http://localhost:3001/update", {
        dorsal: newDorsal,
        posicion: newPosicion,
        id: id
      }).then((response) => {
        setListaJugadores(listaJugadores.map((value) => {
          return value.id === id ? {
            id: value.id, 
            jugador: value.jugador, 
            dorsal: newDorsal, 
            posicion: newPosicion,
          } : value
        }))
      })
    }
  
    const borrarJugador = (id) => {
      axios.delete(`http://localhost:3001/delete/${id}`)
    }

    const uploadProfilePic = (event) => {
      setFile(event.target.files[0])   
      console.log(event.target.files[0])
    }
   
    return (
      <div className="App">
        <div className="form">
          <label className='input-title'>Jugador: </label>
          <input
            type="text"
            name="jugador"
            onChange={(e) => setJugador(e.target.value)}
          />
          <label className='input-title'>Dorsal: </label>
          <input
            type="text"
            name="dorsal"
            onChange={(e) => setDorsal(e.target.value)}
          />
          <label className='input-title'>Posicion: </label>
          <input
            type="text"
            name="posicion"
            onChange={(e) => setPosicion(e.target.value)}
          />
          <input
            type="file"
            name="file"
            onChange={(e) => uploadProfilePic(e)}
          />
          <button className='submit-button' onClick={agregarJugador}>Agregar jugador</button>
          <button className='submit-button' onClick={mostrarJugadores}>Editar jugadores</button>
        </div>
          {listaJugadores.map((value, key) => {
            return (
              <div>
                <div className="card-container">
                  <span className="pro">{value.dorsal}</span>
                  <img className="round" src={imagen} alt="jugador" />
                  <h3>{value.nombre_apellido}</h3>
                  <h6>{value.posicion}</h6>
                  <p>
                    Informacion extra <br /> Estadisticas
                  </p>
                  <div className="buttons">
                    <button className="primary">Estadisticas</button>
                    <button className="primary ghost">+1</button>
                  </div>
                  <div className="skills">
                    <input
                  type='text'
                  value={value.dorsal}
                  placeholder='Nuevo Dorsal'
                  onChange={(event) => {
                    setNewDorsal(event.target.value)
                  }}
                  />
                  <button className="update" onClick={() => {actualizarDorsal(value.id)}}>Update</button>
                  <input
                  type='text'
                  placeholder='Nueva posicion'
                  value={value.posicion}
                  onChange={(event) => {
                    setNewPosicion(event.target.value)
                  }}
                  />
                <button className="update" onClick={() => {actualizarDorsal(value.id)}}>Update</button>
                  <button className="secondary" onClick={() => borrarJugador(value.id)}> x </button> 
                  </div>
                </div> 
              </div>
            );
          })}
      </div>
    );
  }
  
  export default CrearJugador;
