import React from 'react';
import '../App.css';
import { useState, useEffect } from "react";
import imagen from '../assets/logo3.jpg';
import axios from "axios";
import '../App.css'

function Jugadores() {

    const [listaJugadores, setListaJugadores] = useState([]);
  
  
    const mostrarJugadores = () => {
      axios.get("http://localhost:3001/jugadores").then((response) => {
        setListaJugadores(response.data)
      })
    }

    return (
        <div>
            {listaJugadores.map((value, key) => {
            return (
              <div>
                <div className="card-container">
                  <span className="pro">{value.dorsal}</span>
                  <img className="round" src={imagen} alt="jugador" />
                  <h3>{value.nombre_apellido}</h3>
                  <h6>{value.posicion}</h6>
                  <p>
                    Informacion extra <br /> Estadisticas
                  </p>
                  <div className="buttons">
                    <button className="primary">Estadisticas</button>
                    <button className="primary ghost">+1</button>
                  </div>
                  <div className="skills">
                    <input
                  type='text'
                  value={value.dorsal}
                  placeholder='Nuevo Dorsal'
                  onChange={(event) => {
                    setNewDorsal(event.target.value)
                  }}
                  />
                  <button className="update" onClick={() => {actualizarDorsal(value.id)}}>Update</button>
                  <input
                  type='text'
                  placeholder='Nueva posicion'
                  value={value.posicion}
                  onChange={(event) => {
                    setNewPosicion(event.target.value)
                  }}
                  />
                <button className="update" onClick={() => {actualizarDorsal(value.id)}}>Update</button>
                  <button className="secondary" onClick={() => borrarJugador(value.id)}> x </button> 
                  </div>
                </div> 
              </div>
            );
          })}
        </div>
    )
}

export default Jugadores;
