import React from 'react';
import '../App.css';
import { useState, useEffect } from "react";
import axios from "axios";
import { useParams, withRouter } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFutbol } from "@fortawesome/free-solid-svg-icons";
import { faRunning } from "@fortawesome/free-solid-svg-icons";
import { faHandPaper } from "@fortawesome/free-solid-svg-icons";
import '../App.css';

function Jugador() {

    const [perfil, setPerfil] = useState('');

    let { id } = useParams();

    useEffect( () => {
        axios.get(`http://localhost:3001/jugadores/${id}`)
        .then((response) => {
            setPerfil(response.data[0])
            console.log(response.data[0])
        });
    },[])

    const playerInfo = {
        fontSize: "28px",
        color: 'white'
      };

    return (
        <div>
            <ul>
                <h4 style={playerInfo}>{perfil?.nombre_apellido} | {perfil?.posicion} | {perfil?.dorsal}</h4>
                <li>Goles <FontAwesomeIcon icon={faFutbol} /> : {perfil.goles}</li>
                <li>Asistencias: {perfil.asistencias}</li>
                <li>Tarjetas Amarillas: {perfil.t_amarillas}</li>
                <li>Tarjetas Rojas: {perfil.t_rojas}</li>
                <li>Minutos de juego <FontAwesomeIcon icon={faRunning} /> : {perfil.minutos}"</li>
            </ul>
        </div>
    )
}

export default Jugador;
