import React from "react";
import "../App.css";
import { useState, useEffect } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import { Link } from "react-router-dom";
import imagen from "../assets/logo3.jpg";
import axios from "axios";
import "../App.css";
import { useHistory } from "react-router-dom";

function Jugadores() {
  let history = useHistory();

  const [listaJugadores, setListaJugadores] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3001/jugadores").then((response) => {
      setListaJugadores(response.data);
    });
  }, []);

  const plantillaStyle = {
    fontSize: "35px",
  };

  const redirect = () => {
    console.log(listaJugadores);
    history.push(`/jugadores/:id`);
  };

  return (
    <div className="jugadores-title">
      <div class="form__group field">
        <input
          type="input"
          class="form__field"
          placeholder="Name"
          name="name"
          id="name"
          required
        />
        <label for="name" class="form__label">
          Name
        </label>
      </div>
      <h3 style={plantillaStyle}>Plantilla</h3>
      <div className="jugadores-container">
        {listaJugadores.map((value, key) => {
          return (
            <div>
              <div className="card-container">
                <span className="pro">{value.dorsal}</span>
                <img className="round" src={imagen} alt="jugador" />
                <h3>{value.nombre_apellido}</h3>
                <h6>{value.posicion}</h6>
                <p>
                  Informacion extra <br /> Estadisticas
                </p>
                <div className="buttons">
                  <Link to={`/jugadores/${value.id}`}>
                    <button type="submit" className="primary">
                      Estadisticas
                    </button>
                  </Link>

                  <button className="primary ghost">+1</button>
                </div>
                <div className="footer-card"></div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Jugadores;
